function animateHeadline(i){i.each(function(){var i=$(this);setTimeout(function(){hideWord(i.find(".is-visible"))},animationDelay)})}function hideWord(i){var e=takeNext(i);switchWord(i,e),setTimeout(function(){hideWord(e)},animationDelay)}function takeNext(i){return i.is(":last-child")?i.parent().children().eq(0):i.next()}function switchWord(i,e){i.removeClass("is-visible").addClass("is-hidden"),e.removeClass("is-hidden").addClass("is-visible")}var animationDelay=3e3;animateHeadline($(".cta"));